(function() {
    "use strict";

    var btnShowContact = document.getElementById("btn-show-contact-mail");
  console.log(btnShowContact);
    if (btnShowContact) {
        btnShowContact.addEventListener('click', function(evt){
            var elmt = document.createElement('a');
            var addr = ['contact', '@pabanal', '.fr'].join('');
            elmt.setAttribute('href', 'mailto:' + addr);
            elmt.innerText = addr;
            if(this.nextSibling != undefined) {
                this.parentNode.insertBefore(elmt, this.nextSibling);
            } else {
                this.parentNode.appendChild(elmt);
            }
            this.parentNode.removeChild(this);
        }, false);
    }

    var btnShowTel = document.getElementById("btn-show-contact-tel");
    if (btnShowTel) {
        btnShowTel.addEventListener('click', function(evt){
            var elmt = document.createElement('a');
            var tel = ['06', '89', '73', '05', '35'];
          	elmt.setAttribute('href', 'tel:' + tel);
            elmt.innerText = tel.join(' ');
            if(this.nextSibling != undefined) {
                this.parentNode.insertBefore(elmt, this.nextSibling);
            } else {
                this.parentNode.appendChild(elmt);
            }
            this.parentNode.removeChild(this);
        }, false);
    }

})();
